const int N=1010,mod=1e9+7;
int n,t[3]={};
long long ans=1,a,b,c;
signed main(){
	int T;
	// T=read();
	// while(T--){
		t[1]=t[2]=0;
		ans=1;
	n=read();
		for(int i=1,x=0;i<=n;++i){
			x=read();
			++t[x];
		}
		for(int i=t[1]+1;i<=t[1]+t[2];++i){
			ans=ans*i%mod;
    }
		a=1,b=1;
		for(int i=2;i<=t[1];++i){
			c=(b+(i-1)*a)%mod;
			a=b,b=c;
		}
	printf("%lld",ans*b%mod);
	// }
