#include<bits/stdc++.h>
using namespace std;
int main(){
    long long n,k;
    scanf("%I64d%I64d",&n,&k);
    if(n%2==1){
        long long t=(n-1)/2+1;
        if(t>=k){
            long long j=-1;
            for(long long i=1;i<=k;i++){
                j+=2;
            }
            cout<<j;
        }
        else{
            long long j=0;
            for(long long i=1;i<=k-t;i++){
                j+=2;
            }
            cout<<j;
        }
    }
    else{
        long long t=(n-2)/2+1;
        if(t>=k){
            long long j=-1;
            for(long long i=1;i<=k;i++){
                j+=2;
            }
            cout<<j;
        }
        else{
            long long j=0;
            for(long long i=1;i<=k-t;i++){
                j+=2;
            }
            cout<<j;
        }
    }
    return 0;
}
