{
	cin>>n>>m;
	for(int i=1;i<=m;i++)
	{
		cin>>a>>b;
		v[a]++;
		v[b]++;
	}
	int q;
	for(int i=1;i<=n;i++)
	{
		if(v[i]==0)
		{
			q=i;
			break;
		  }  
	}
	cout<<n-1<<endl;
	for(int i=1;i<=n;i++)
	{
		if(i!=q)
		  cout<<i<<" "<<q<<endl;
	}
}
