//本题求出两个时间的差(上减下)
#include<bits/stdc++.h>
using namespace std;
int s,t,p;
string strs,strt,strp;
int main(){
    cin>>strt>>strs;
    s=600*(strs[0]-48)+60*(strs[1]-48)+10*(strs[3]-48)+strs[4]-48;
    t=600*(strt[0]-48)+60*(strt[1]-48)+10*(strt[3]-48)+strt[4]-48;
    //把读入的时间转化成分钟数
    p=t-s;
    if(p<0) p+=1440;
    //算出睡觉的分钟数,注意若睡了一夜要加上一天(1440分钟)
    printf("%c",p/600+48);
    p%=600;
    printf("%c",p/60+48);
    p%=60;
    printf(":");
    printf("%c",p/10+48);
    p%=10;
    printf("%c",p+48);
    //输出时间
    return 0;
}
