        else
            if(s[i] != '?'){
                if(now[i] == -1)
                    NO
                now[i] = 1;
                if(s[i] == '2')
                    if(now[i - 1] == 1)
                        NO
                    else
                        now[i - 1] = now[i + 1] = -1;
                else
                    if(s[i] == '0')
