{
	cin>>s1>>s2;
	int score1=0,score2=0;
	int len=s1.size();
	for(int i=0;i<len;i+=2)
	{
		int a1,a2;//1是剪刀，2是石头，3是布 
		if(s1[i]=='8' && s1[i+1]=='<')a1=1;
		if(s1[i]=='(' && s1[i+1]==')')a1=2;
		if(s1[i]=='[' && s1[i+1]==']')a1=3;
		if(s2[i]=='8' && s2[i+1]=='<')a2=1;
		if(s2[i]=='(' && s2[i+1]==')')a2=2;
		if(s2[i]=='[' && s2[i+1]==']')a2=3;
		if(a1==1)
		{
			if(a2==2)score2++;
			if(a2==3)score1++;
		}
		if(a1==2)
		{
			if(a2==1)score1++;
			if(a2==3)score2++;
		}
		if(a1==3)
		{
			if(a2==1)score2++;
			if(a2==2)score1++;
		}
	}
	if(score2==score1)cout<<"TIE";
	if(score1>score2)cout<<"TEAM 1 WINS";
	if(score2>score1)cout<<"TEAM 2 WINS"; 
	return 0;
}
```

