	{
		getchar();//忽略第一个字符
		c=getchar();//省去c2数组，减少空间
		if(c1[i]==']')//第一个人是布
		{
			if(c==')')poin1++;//第二个人是石头
			if(c=='<')poin2++;//第二个人是剪刀
		}
		if(c1[i]==')')//第一个人是石头
		{
			if(c=='<')poin1++;//第二个人是剪刀
			if(c==']')poin2++;//第二个人是布
		}
		if(c1[i]=='<')第一个人是剪刀
