    while ('0'<=ch && ch<='9'){x=(x<<3)+(x<<1)+(ch^48);ch=getchar();}
    return x*f;
}
db a,b,c;
db h(db a,db b){
	double d= sqrt(a*a +b*b); //计算斜边长
	return (a*b/ d); 
}

int main(){
	ios_base::sync_with_stdio(false);cin.tie(0);
	cin>>c;
	for(a=1;a<=10;a++){
		for(b=1;b<=10;b++){
			if(abs(c - h(a / 2, b))<=1e-6){//有误差
				cout<<a<<" "<<b;
				return 0;
			}
		}
	}
	return 0;
}


