```
#include<iostream>
#include<queue>
#define ll long long
using namespace std;
ll n,m,s=1;
struct Edge{
	int next,to,dist;
}edge[5000];
int cmp=0;
int head[5000],dist[5000];
bool visi[5000];
void add(ll from,ll to,ll dis)
{
    edge[++cmp].next=head[from];
    edge[cmp].to=to;
    edge[cmp].dist=dis;
    head[from]=cmp;
}
queue<ll> duilie; 
void spfa()
{
    for(ll i=1;i<=n;i++)
    {
        dist[i]=0x7fffffff;
        visi[i]=false;
    }
    duilie.push(s);dist[s]=0;visi[s]=true;
    while(duilie.empty()==false)
    {
        ll j=duilie.front();
        duilie.pop();visi[j]=0;
        for(ll i=head[j];i;i=edge[i].next)
        {
            ll w=edge[i].to;
            if(dist[w]>dist[j]+edge[i].dist)
            {
                dist[w]=dist[j]+edge[i].dist;
                if(visi[w]==false)
                {
                    visi[w]=true;
                    duilie.push(w);
                }
            }
        }
    }
}
int main()
{
    s=1;
    n=3;m=2;
    for(ll i=1;i<=m;i++)
    {
        ll a;
        cin>>a;
        add(i,i+1,a);
    }
    spfa();
    cout<<dist[3];
    return 0;
}
```

