#include<iostream>
#include<cstdio>

using namespace std;
#define LL long long
struct node{
    LL sum,l,r;
    node *LEFT,*RIGHT;
}tree;
LL n,index,a,b;
int build(node *cur,int L,int R)
{
    if(L<R-1)
    {
        cur->LEFT=new node;
        cur->RIGHT=new node;
            cur->sum=build(cur->LEFT,L,(L+R)/2)+build(cur->RIGHT,(L+R)/2,R);
    }
    else
        return cur->sum=++index;
}
int sum(int n)
{
    build(&tree,1,n+1);
    index=0;
    return tree.sum;
}
inline void Init()
{
    cin>>a>>b;
}
inline void Work()
{
    cout<<sum(a)-sum(a-1)+sum(b)-sum(b-1);
}
int main()
{
    Init();
    Work();
    return 0;
}
