#include<bits/stdc++.h>
using namespace std;
const int maxn=1000;
//大数据高精  zzst
struct bign{
	int a[maxn];
	int len;
	//分别表示位和位数

	bign(){
		memset(a,0,sizeof(a));
		len=1;
	}

	//赋值运算 
	bign operator = (const char *s){
		len=strlen(s);
		for(int i=0;i<len;i++){
			a[i]=s[len-i-1]-'0';
		}
		return *this;
	}

	//进位运算 
	void step(){
		for(int i=0;i<len;i++){
			a[i+1]+=a[i]/10;
			a[i]%=10;
		}
		//如果进位到len了 
		while(a[len]){
			a[len+1]+=a[len]/10;
			a[len]%=10;
			len++;
		}
		if(len==0) len=1;
	}

	//输出这个数
	void output(){
		for(int i=len-1;i>=0;i--) cout<<a[i];
		cout<<endl;
	}
};
//<运算 
bool operator < (const bign &a,const bign &b){
	if(a.len!=b.len) return a.len<b.len;
	for(int i=a.len-1;i>=0;i--){
		if(a.a[i]!=b.a[i]) return a.a[i]<b.a[i];
	} 
	return 0;
}
//偷懒的>=运算 
bool operator >= (const bign &a,const bign &b){
	return !(a<b);
}

//==
bool operator == (const bign &a,const bign &b){
	if(a.len!=b.len) return 0;
	for(int i=0;i<a.len;i++){
		if(a.a[i]!=b.a[i]) return 0;
	}
	return 1;
}

//>
bool operator > (const bign &a,const bign &b){
	return (!(a<b)&&!(a==b));
}

//<=
bool operator <=(const bign &a,const bign &b){
	return !(a>b);
}

//高精+高精 
bign operator + (const bign &a,const bign &b){
	bign c;
	int len=max(a.len,b.len);
	for(int i=0;i<len;i++){
		c.a[i]=a.a[i]+b.a[i];
	}
	c.len=len;
	c.step();
	return c;
}

//高精-高精 保证 a>b 
bign operator - (const bign &a,const bign &b){
	bign c;
	int len=a.len;
	for(int i=0;i<len;i++) c.a[i]=a.a[i]-b.a[i];
	//处理退位
	for(int i=0;i<len;i++){
		if(c.a[i]<0){
			c.a[i]+=10;
			c.a[i+1]--;
		}
	}
	while(len>1&&c.a[len-1]==0) len--;
	c.len=len;
	return c;
}

//高精*高精
bign operator * (const bign &a,const bign &b){
	bign c;
	c.len=a.len+b.len-1;
	for(int i=0;i<a.len;i++){
		for(int j=0;j<b.len;j++){
			c.a[i+j]+=a.a[i]*b.a[j];
		}
	}
	c.step();
	return c;
} 

//高精+int
bign operator + (const bign &a,int b){
	bign c;
	c=a;
	c.a[0]+=b;
	c.step();
	return c;
}

//高精*int
bign operator * (const bign &a,int b){
	bign c;
	c.len=a.len;
	for(int i=0;i<c.len;i++) c.a[i]=a.a[i]*b;
	c.step();
	return c;
} 

//高精除法和取模
//此算法是基于之前所有的 + - *的，所以比他们更复杂 
bign operator / (const bign &a,const bign &b){
	bign c;//商 
