#include<bits/stdc++.h>
using namespace std;
int n,v,i,k,p,s,j,flag,a[51];
int main(){
	cin>>n>>v;
	for(i=1;i<=n;i++){
		cin>>k;
		flag=0;//标记是否有小于v的
		for(j=1;j<=k;j++){
			cin>>s;
			if(s<v) flag=1;//判断
		}
		if(flag) p++,a[i]=1;//统计个数,哪个人
	}
	cout<<p<<endl;//总个数
	for(i=1;i<=n;i++){
		if(a[i]) cout<<i<<' ';//输出编号
	}
	return 0;
