#include <bits/stdc++.h>

#define enter putchar('\n')
#define space putchar(' ')
#define re register
#define N 510
#define M 1010

using namespace std;

inline int max(int x,int y){return (x>y?x:y);}

inline int min(int x,int y){return (x<y?x:y);}

inline int read(){
	int x=0;char c=getchar();bool y=1;
	for(;c<'0' || c>'9';c=getchar()) if(c=='-') y=0;
	for(;c>='0' && c<='9';c=getchar()) x=(x<<1)+(x<<3)+c-48;
	if(y) return x;
	return -x;
}

int n,m,K;

int d[N],st[N],top;

double v[N][N];

bool V[N];

struct matrix{
	double mat[N][N];
	inline double * operator [] (const int a){return mat[a];}
	matrix(){memset(mat,0,sizeof(mat));return;}
	matrix operator * (const matrix &a){
		matrix C;
		for(re int i=1;i<=n;++i)
			for(re int j=1;j<=n;++j)
				for(re int k=1;k<=n;++k)
				C.mat[i][j]+=mat[i][k]*a.mat[k][j];
		return C;
	}
	matrix operator + (const matrix &a){
		matrix C;
		for(re int i=0;i<=top;++i)
			for(re int j=0;j<=top;++j)
				for(re int k=0;k<=top;++k)
				C.mat[i][j]+=mat[i][k]*a.mat[k][j];
		return C;
	}
}I,U,A;

double a[N][M];

inline matrix In(matrix &b){
	matrix C;
	for(re int i=1;i<=n;++i){
		for(re int j=1;j<=n;++j)
		a[i][j]=b[i][j],a[i][j+n]=0;
		a[i][i+n]=1;
	}

	for(re int i=1;i<=n;++i){
		for(re int j=i;j<=n;++j)
		if(a[j][i]){
			if(j^i)
			for(re int k=1;k<=(n<<1);++k)
			swap(a[i][k],a[j][k]);
			break;
		}
		double r=1./a[i][i];
		for(re int j=1;j<=(n<<1);++j)
		a[i][j]=a[i][j]*r;
		for(re int j=1;j<=n;++j)if(j^i){
			r=a[j][i];
			for(re int k=i;k<=(n<<1);++k)
			a[j][k]-=r*a[i][k];
		}
	}

	for(re int i=1;i<=n;++i)
		for(re int j=1;j<=n;++j)
		C[i][j]=a[i][j+n];
	return C;
}

inline void Input(){
	n=read(),m=read(),K=read();
	for(re int i=1;i<=n;++i){
		V[i]=read();
		if(V[i]) st[++top]=i;
	}
	for(re int i=1;i<=m;++i){
		int u=read(),w=read();
		++v[u][w];
		++v[w][u];
		++d[u],++d[w];
	}
	return;
}

inline void solve(){
	for(re int i=1;i<=n;++i) U[i][i]=1;
	for(re int i=1;i<=n;++i)
		for(re int j=1;j<=n;++j)
		if(V[j])	I[i][j]=v[i][j]/d[i];
		else		U[i][j]-=v[i][j]/d[i];
	U=In(U);
	U=U*I;
	/*for(re int i=1;i<=n;++i){
		for(re int j=1;j<=n;++j)
		cout<<U[i][j]<<" ";
		enter;
	}*/
	for(re int i=1;i<=top;++i)
	A[0][i]=U[1][st[i]];

	for(re int i=1;i<=top;++i)
		for(re int j=1;j<=top;++j)
		I[i][j]=U[st[i]][st[j]];

	K-=2;
	while(K){
		if(K&1) A=A+I;
		I=I+I;
		K>>=1;
	}
	printf("%.8lf\n",A[0][top]);
	return;
}

int main(){
	freopen("data.in","r",stdin);
	freopen("data.out","w",stdout);
	Input();
	solve();
	return 0;
}
