#include<bits/stdc++.h>
using namespace std;
#define ll long long
ll n,m,k;
int main(){
	cin>>n>>m>>k;
    if(k>n+m-2){
    	cout<<"-1";	return 0;
	} //切不了的情况
    //众所周知求面积是长乘宽
	if(k<m){
		if(k<n){
			cout<<max(n*(m/(k+1)),m*(n/(k+1)));	
		}//情况1
		else{
			cout<<max(n*(m/(k+1)),m/(k-n+2));
		}//情况2
	} 
	else{
		if(k<n){
			cout<<max(n/(k-m+2),m*(n/(k+1)));
		}//情况3
		else{
			cout<<max(n/(k-m+2),m/(k-n+2));
		}//情况4
	}
    return 0;
}
