	int u,v,w;
	memset(vis,0,sizeof(vis));
	memset(head,-1,sizeof(head));
	scanf("%d%d%d",&n,&m,&k);
	for(int i=1;i<=n;i++)
		dis[i]=INF;
	for(int i=1;i<=m;i++){
		scanf("%d%d%d",&u,&v,&w);
		E[u].push_back(make_pair(v,w));
        E[v].push_back(make_pair(u,w));
	}
	q.push(1);
	dis[1]=0;
	vis[1]=1;
	for(int i=1;i<=k;i++){
		scanf("%d%d",&v,&w);
		if(w<dis[v]){
			dis[v]=w;
			vis[v]=1;
			book[v]=1;
			q.push(v);
		}
	}
	while(!q.empty()){
		int u=q.front();
		q.pop();
		vis[u]=0;
		for(int i=0;i<E[u].size();i++){
			int v=E[u][i].first;
            int w=E[u][i].second;
			if(dis[v]>=dis[u]+w){
				dis[v]=dis[u]+w;
				book[v]=0;
				if(!vis[v]){
					vis[v]=1;
					q.push(v);
				}
			}
		}
	}
