inline int ksm(int x,int y)
{
	int ans = 1;
	while(y)
	{
		if(y&1) ans = 1ll * ans * x % mod;
		y >>= 1;x = 1ll * x * x % mod;
	}
	return ans;
}

inline void doit(int F)
{
	rep(i,0,19)
		repd(j,s,0)
			if((j&(1<<i))==0)
				f[j] = (f[j] + f[j|(1<<i)] * F %mod + mod)%mod;
}

int main()
{
	scanf("%d",&n);	
	rep(i,1,n) { int x;scanf("%d",&x); ++ f[x]; }
	s = (1 << 20) - 1;
	doit(1);
	rep(i,0,s) f[i] = ksm(2,f[i]) - 1;
	doit(-1);
	cout << f[0] << endl;
