#include<iostream>
#include<cstring>
#include<cmath>
#include<set>
using namespace std;

struct bbyy
{
    string name;
    string color;
    bool operator==(bbyy &b) 
    {
        return (name==b.name&&color==b.color);
    }
}b[101];//结构体，运算符重载可以减少代码量

int main()
{
    int n;
    cin>>n;

    for(int i=1;i<=n;i++)
        cin>>b[i].name>>b[i].color;//读入

    int t=0;
    struct bbyy p[101];//存储已经选出的叶子
    for(int i=1;i<=n;i++)
    {
        int f=false;
        for(int j=1;j<=t;j++)if(p[j]==b[i]){f=true;break;}
        if(f)continue;//如果和之前选中的完全一样就不选

        t++;
        p[t]=b[i];//选中并存储
    }
    cout<<t;//输出
    return 0;
}

