	if(n==8)cout<<"vaporeon";//第一类，字符串长度为八
	if(n==6)cout<<"espeon";//第二类，字符串长度为六
	if(n==7){//第三类，字符串长度为七
	if(a[0]=='j'||a[1]=='o'||a[3]=='t'){
	cout<<"jolteon";}
    if(a[0]=='f'||a[1]=='l'||a[3]=='a'){
    cout<<"flareon";}
    if(a[0]=='u'||a[1]=='m'||a[2]=='b'){
	cout<<"umbreon";}
    if(a[0]=='l'||a[1]=='e'||a[3]=='f'){
	cout<<"leafeon";}
    if(a[0]=='s'||a[1]=='y'||a[3]=='v'){
	cout<<"sylveon";}
	if(a[1]=='l'){//特判，当第二个字符为l时
	if(a[0]=='g'||a[3]=='c'){
	cout<<"glaceon";}
	if(a[0]=='f'||a[3]=='a'){
    cout<<"flareon";}}
    }
    return 0;//完美结束
    }
```
