			if(y=='>'){
				ax++;
			}
			else{
				ad++;
			}
		}
		else if(z=='B'){
			if(y=='>'){
				bx++;
			}
			else{
				bd++;
			}
		}
		else if(z=='C'){
			if(y=='>'){
				cx++;
			}
			else{
				cd++;
			}
		}
	}
	if(ad==1){
		a[2] = 1;
	}
	else if(ad==2){
		a[1] = 1;
	}
	else{
		a[3] = 1;
	}
	if(bd==1){
		b[2] = 1;
	}
	else if(bd==2){
		b[1] = 1;
	}
	else{
		b[3] = 1;
	}
	if(cd==1){
		c[2] = 1;
	}
	else if(cd==2){
		c[1] = 1;
	}
	else{
		c[3] = 1;
	}
	int xyz=0;
	for(int i=1;i<=3;i++){
		if(a[i]==1) xyz++;
		if(b[i]==1) xyz++;
		if(c[i]==1) xyz++;
