    {
    	if(ch1[0]=='A'){a++;}
    	if(ch1[0]=='B'){b++;}
    	if(ch1[0]=='C'){c++;}
    }
    if(ch1[1]=='>')
    {
    	if(ch1[2]=='A'){a++;}
    	if(ch1[2]=='B'){b++;}
    	if(ch1[2]=='C'){c++;}
    }
    if(ch2[1]=='<')
    {
    	if(ch2[0]=='A'){a++;}
    	if(ch2[0]=='B'){b++;}
    	if(ch2[0]=='C'){c++;}
    }
    if(ch2[1]=='>')
