	//freopen(".in","r",stdin);
	//freopen(".out","w",stdout);
	//freopen(".ans","w",sdtout);
	read(m);
	for(i=1;i<=m;i++)
	scanf("%s",ch[i]+1);

	n=strlen(ch[1]+1);

	for(i=1;i<=m;i++)
	for(j=i+1;j<=m;j++)
	{
		int mask=0;
		for(k=1;k<=n;k++)
		if(ch[i][k]==ch[j][k])
		mask|=1<<k-1;

		p[mask]|=1ll<<j-1,p[mask]|=1ll<<i-1;
	}
	for(i=1;i<1<<n;i++)
	cnt[i]=__builtin_popcount(i);

