	{
		for(i=1;i<=n;i++)
		if(((1<<i-1)&mask)==0)
		{
			p[mask]|=p[mask^(1<<i-1)];
			dp[mask]+=1.0/(n-cnt[mask])*dp[mask^(1<<i-1)];
