#include <algorithm>
using namespace std;

int n, a[1000007], cplusplus[1000007], math[1000007], pe[1000007], cnt1, cnt2, cnt3, ans;

int main() {
	scanf("%d", &n);
	for(int i = 1; i <= n; ++i) {
		scanf("%d", &a[i]);
		if(a[i] == 1)	cplusplus[++cnt1] = i;
		if(a[i] == 2)	math[++cnt2] = i;
		if(a[i] == 3)	pe[++cnt3] = i;
	}
	ans = min(cnt1, min(cnt2, cnt3));
	printf("%d\n", ans);
	for(int i = 1; i <= ans; ++i) {
		printf("%d %d %d\n", cplusplus[i], math[i], pe[i]);
	}
	return 0;
}
```

