{
    char c;
    x = 0;
    int fu = 1;
    c = getchar();
    while(c > 57 || c < 48)
    {
        if(c == 45)
        {
            fu = -1;
        }
        c = getchar();
    }
    while(c <= 57 && c >= 48)
    {
        x = (x << 3) + (x << 1) + c - 48;
        c = getchar();
    }
    x *= fu;
}
template <typename T>
inline void fprint(T x)
{
    if(x < 0)
    {
        putchar(45);
        x = -x;
    }
    if(x > 9)
    {
        fprint(x / 10);
    }
    putchar(x % 10 + 48);
}
int n;
int a[1000010], ans[200010], cur[2];
bool b[1000010];
int main()
{
	read(n);
	for (register int i = 1;i <= n;i ++)
	{
		int u, v;
		read(u);read(v);
		a[u] = v;
		if(v == 0) a[u] = -1;
		b[v] = true;
	}
	for (register int i = 1;i <= 1000000;i ++)
	{
		if(a[i] && !b[i])
		{
			cur[1] = i;
			break;
		}
	}
	cur[0] = a[0];
	for (register int i = 1;i <= n;i ++)
	{
		fprint(cur[i & 1]);
		cur[i & 1] = a[cur[i & 1]];
		putchar(32);
