    if k<1 then no else yes;
  end;
  if a[lx,ly] then//如果终点是浮冰，那么需要从一个浮冰过来，再去一个浮冰跳一下
  begin
    k:=0;
    for i:=1 to 4 do
    if a[lx+z[i,1],ly+z[i,2]] then inc(k);
    if k<2 then no;//浮冰数>=2有可能才行
  end else
  a[lx,ly]:=true;//终点赋值为true
  h:=1;
  t:=1;
  x[1]:=fx;
  y[1]:=fy;
