```cpp
#include<bits/stdc++.h>
using namespace std;

int read() {
	int xx=0,ww=1;
	char ch=getchar();
	while(ch>'9'||ch<'0') {
		if(ch=='-') ww=-ww;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9') {
		xx=10*xx+ch-48;
		ch=getchar();
	}
	return xx*ww;
}

int n,m,tmp;
char ch,mp[510][510];
int sx,sy,ex,ey;
bool vis[510][510];
bool flag;
int dx[]= {0,1,-1,0},dy[]= {1,0,0,-1};

bool check(int x,int y) {
	if(x==ex&&y==ey) return true;
	if(x>n||y>m||x<1||y<1||vis[x][y]) return false;
	if(mp[x][y]=='X') return false;
	return true;
}

void dfs(int x,int y) {
	if(flag) return ;
	vis[x][y]=true;
	if(x==ex&&y==ey) {
		flag=true;
		puts("YES");
		exit(0);
	}
	for(int i=0; i<4; i++) {
		int xx=x+dx[i];
		int yy=y+dy[i];
		if(check(xx,yy)) dfs(xx,yy);
		if(xx==ex&&yy==ey) {
			flag=true;
			puts("YES");
			exit(0);
		}
	}
}

int main() {
	memset(vis,false,sizeof(vis));
	n=read();
	m=read();
	for(int i=1; i<=n; i++) {
		for(int j=1; j<=m; j++) {
			ch=getchar();
			mp[i][j]=ch;
		}
		getchar();
	}
	sx=read();
	sy=read();
	ex=read();
	ey=read();
	for(int i=0; i<4; i++) {
		int X=ex+dx[i];
		int Y=ey+dy[i];
		if(check(X,Y)) tmp++;
		if(X==sx&&Y==sy) tmp++;
	}
	if(sx==ex&&sy==ey) {
		if(tmp>0) puts("YES");
		else puts("NO");
		return 0;
	} else if(mp[ex][ey]=='.') {
		if(tmp<2) {
			puts("NO");
			return 0;
		}
	} else if(mp[ex][ey]=='X') {
		if(tmp<1) {
			puts("NO");
			return 0;
		}
	}
	flag=false;
	for(int i=0; i<4; i++) {
		int X=sx+dx[i];
		int Y=sy+dy[i];
		if(check(X,Y)) dfs(X,Y);
	}
	if(!flag) puts("NO");
	return 0;
}
```

一道$DFS$爆搜题。

如果终点是$"."$ 看有无两个通它的点。

如果终点是$"X"$ 看有无一个通它的点。

起点也算一个点！！！

如果可以，$DFS$

不行，输出$NO$。

$DFS$可以，输出$YES$

否则，$NO$

这是简单的dfs,可以看下dfs的写法,还是比较经典
```cpp
#include <bits/stdc++.h>
using namespace std;
const int maxn = 510;
int n,m;
char G[maxn][maxn];
int vis[maxn][maxn];
int dir[4][2] = {{0,-1},{0,1},{1,0},{-1,0}};
int sx,sy,ex,ey;
inline bool inG(int x,int y){
	return x >= 1 && x <= n && y >= 1 && y <= m;
}
bool dfs(int nowx,int nowy){
	//cout << "vis:" << nowx << " " << nowy << endl; 
	for(int i=0;i<4;i++){
		int tx = nowx + dir[i][0];
		int ty = nowy + dir[i][1];
		if(!inG(tx,ty)) continue;
		if(vis[tx][ty] == 1){
			//如果破裂处
			if(tx == ex && ty == ey) return true;
			else continue; 
		}else{
			vis[tx][ty] = 1;
			if(dfs(tx,ty)) return true;
			//vis[tx][ty] = 0;
		}
	}
	return false;
}
int main(){
	ios::sync_with_stdio(0);
	cin.tie(0);
	cin >> n >> m;
	memset(vis,0,sizeof vis);
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			cin >> G[i][j];
			if(G[i][j] == 'X') vis[i][j] = 1;
		}
	}
	cin >> sx >> sy >> ex >> ey;
	if(dfs(sx,sy)) cout << "YES" << endl;
	else cout << "NO" << endl;
	return 0;
}
```

算法：dfs                                        
这是一道很好的dfs题目，思路很简单：即把所有的点给dfs一遍，再是否能到达终点，然后再做处理。             
话不多说，附上代码：
```cpp
#include<bits/stdc++.h>
using namespace std;
const int N=505;
bool used[N][N];
int dx[]={0,1,0,-1};
int dy[]={1,0,-1,0};
int n,m,a1,a2,b1,b2,use;
char c[N][N];
inline bool ok(int x2,int y2){return (x2>=0 && y2>=0 && x2<n && y2<m);}//判断是否在表格内 
inline void dfs(int x,int y)//dfs看是否能从起点到这个点 
{
	used[x][y]=true;
	for(int i=0;i<4;i++)
	{
		int x2=x+dx[i];
		int y2=y+dy[i];
		if(ok(x2,y2)&&c[x2][y2]=='.'&&!used[x2][y2])dfs(x2,y2);
	}
}
inline bool check()//判断是否能到达终点并且掉进去 
{
	int res=0;
	for(int i=0;i<4;i++)
	{
		int x2=a2+dx[i];
		int y2=b2+dy[i];
		if(ok(x2,y2)&&used[x2][y2])res++;
	}
	return res>=2||(res>=1&&c[a2][b2]=='X');
}
int main()//主函数，输入输出
{
	cin>>n>>m;
	for(int i=0;i<n;i++)cin>>c[i];
	cin>>a1>>b1>>a2>>b2;
	a1--,b1--,a2--,b2--;
	dfs(a1,b1);
	bool judge=check();
	if(judge)
		cout<<"YES\n";
	else
		cout<<"NO\n";
	return 0;
} 
```
