	if  (abs(x - x2) == n)
		cout << n+min(2*n-(y +y2),y +y2) << endl;
	elif(abs(y - y2) == n)
		cout << n+min(2*n-(x +x2),x +x2) << endl;
	else cout << abs(x  - x2) + abs(y  - y2) << endl;
