int Max(int a,int b)//取max 
{
	if(a<b) return b;
	return a;
}

int Min(int a,int b)//取min 
{
	if(a<b) return a;
	return b; 
}

int main ()
{
    s(x),s(y);
    int a=Min(x,y);//可以穿不同的袜子的最大天数
    int b=(Max(x,y)-a)/2;//可以穿同样的袜子的天数
   	p(a),p(b);
    return 0*puts("");
}
