这题不是很难，就是要认真点

~~~
#include<bits/stdc++.h>
using namespace std;
int main()
{
	int n;
	cin>>n;//输入 
	if(n%2==1)//如果n为奇数，则不存在 
	{
		cout<<0;
		return 0;
	}
	n=n/2;//一个长边和一个短边长度之和 
	n++;
	cout<<n/2-1;//计算其数量 
	return 0;
}

