#include<bits/stdc++.h> 
#define ull unsigned long long
using namespace std;
map<ull,bool>a;
int main( ){
	ull n,ans=0,i,j,k;
	std::ios::sync_with_stdio(false);
	cin>>n;
	for(i=1;i<=n;i++){
		cin>>k;         //输入一个处理一个
		while(a[k]&&k>=1)k--;      //如果已经被用过就--，因为
                                   不管是哪一个--，代价均为1
		a[k]=1;                    //a[k]=1，标记k已经有了
		ans+=k;                    //ans加上k
	}
	cout<<ans<<endl;               //华丽结束
}
```

