	cin >> n;
	int t = 0, ans = 0;
	for (int i = 1; i <= n; i++) {
		int x;
		cin >> x;
		t |= x;
	}
	ans += t;
	t = 0;
	for (int i = 1; i <= n; i++) {
		int x;
		cin >> x;
		t |= x;
	}
	ans += t;
	cout << ans << endl;
}
