	}
	int t1 = lower_bound(a, a+450000, n)-a;  //返回指向大于等于n的第一个值的位置。
	int t2 = upper_bound(a, a+450000, n)-a;  //返回指向大于n的第一个值的位置。
	if(t1 == t2) //如果没找到……
	{
		cout << 0 << endl;
		return 0;
	}
	cout << t2-t1 << endl; //输出结果。
	for(i = t1; i < t2; i++)
		cout << i << " ";
	cout << endl;
