题意简述：给定一张 $n$ 个点 $m$ 条边的初始无向图。

$q$ 次询问，每次询问给定一个点集 $V$ 和边集 $E$。

你需要判断，将 $E$ 中的边加入初始无向图之后，$V$ 中任意两个点 $x,y$ 是否都能在每条边至多经过一次的情况下从 $x$ 到 $y$ 再回到 $x$。

$n,m,q,\sum|V|,\sum |E| \le 3 \times 10^5$，强制在线。

这题比较简单，无非就是问你这些点是否在同一个点双里。

先把原图缩边双，变成一颗树，对于每一个询问，把给定的点和连接的边的两端加入虚森林中，然后再将新加的边加入虚森林中，再跑一次边双联通分量就好了。

注意点：
 - 原图不联通。
 - 本题有重边，处理边双的时候要注意一点。
 - 建虚森林的时候要注意对每一个联通块建虚树。
 - 这题有强制在线，不要忘记处理了。
 - 本题时无向图，虚森林连边要连两次。

Code：
```cpp
#include<bits/stdc++.h>
using namespace std;
const int maxn=670000;
int n,m,q,u,v;
int h[maxn],k=0,st[maxn],top=0;
int f[maxn][20],dep[maxn],id[maxn],idn=0;
int N,M,x[maxn],a[maxn],b[maxn];
int tot=1,pre[maxn],now[maxn],son[maxn];
int dfn0[maxn],num=0,low0[maxn],br0[maxn],bi[maxn],sum=0;
int dfn[maxn],low[maxn],vis[maxn];
int fa[maxn],rt[maxn];
int R=0;
vector<int> g[maxn];
multiset<int> G[maxn];
set<int> G0[maxn];
void put(int x,int y)
{
	pre[++tot]=now[x];
	now[x]=tot;
	son[tot]=y;
}
int find(int x){return x==fa[x]?x:fa[x]=find(fa[x]);}
void Union(int x,int y){fa[find(x)]=find(y);}
int LCA(int x,int y)
{
	if(find(x)!=find(y))
		return -1;
	if(dep[x]<dep[y])
		swap(x,y);
	for(int i=19;i>=0;i--)
		if(dep[f[x][i]]>=dep[y])
			x=f[x][i];
	if(x==y)
		return x;
	for(int i=19;i>=0;i--)
		if(f[x][i]!=f[y][i])
			x=f[x][i],y=f[y][i];
	return f[x][0];
}
void tarjan0(int x,int last)
{
	dfn0[x]=low0[x]=++num;
	for(int p=now[x];p;p=pre[p])
	{
		int t=son[p];
		if(!dfn0[t])
		{
			tarjan0(t,p);
			low0[x]=min(low0[x],low0[t]);
			if(low0[t]>dfn0[x])
				br0[p]=br0[p^1]=1;
		}
		else if(p!=(last^1))
			low0[x]=min(low0[x],dfn0[t]);
	}
}
void tarjan(int x,int last)
{
	dfn[x]=low[x]=++num;
	int ok=0;
	for(auto t:G[x])
	{
		if(!dfn[t])
		{
			tarjan(t,x);
			low[x]=min(low[x],low[t]);
		}
		else
		{
			if(t==last&&ok==0)
				ok=1;
			else
				low[x]=min(low[x],dfn[t]);
		}
	}
}
void getvis(int x)
{
	vis[x]=1;
	for(auto t:G[x])
		if(!vis[t]&&((dfn[t]>dfn[x]&&low[t]<=dfn[x])||(dfn[t]<dfn[x]&&low[x]<=dfn[t])))
			getvis(t);
}
void sd(int x)
{
	bi[x]=sum;
	for(int p=now[x];p;p=pre[p])
	{
		int t=son[p];
		if(bi[t])
			continue;
		if(br0[p])
		{
			g[sum].push_back(t);
			continue;
		}
		sd(t);
	}
}
void dfs(int x,int fa,int Dp,int Rt)
{
	f[x][0]=fa;id[x]=++idn;dep[x]=Dp;rt[x]=Rt;
	for(int i=1;i<=19;i++)
		f[x][i]=f[f[x][i-1]][i-1];
	for(auto t:G0[x])
	{
		if(t==fa)
			continue;
		dfs(t,x,Dp+1,Rt);
	}
}
bool cmp(int x,int y){return id[x]<id[y];}
void build()
{
	sort(h+1,h+k+1,cmp);
	for(int i=1;i<=k;i++)
	{
		dfn[h[i]]=low[h[i]]=vis[h[i]]=0;
		if(i==1||LCA(h[i],h[i-1])==-1)
		{
			for(int j=1;j<top;j++)
				G[st[j]].insert(st[j+1]),G[st[j+1]].insert(st[j]);
			G[rt[h[i]]].clear(),st[top=1]=rt[h[i]];
		}
		if(h[i]!=rt[h[i]])
		{
			int Lca=LCA(st[top],h[i]);
			dfn[Lca]=low[Lca]=vis[Lca]=0;
			if(Lca!=st[top])
			{
				while(id[st[top-1]]>id[Lca])
					G[st[top-1]].insert(st[top]),G[st[top]].insert(st[top-1]),top--;
				if(st[top-1]!=Lca)
					G[Lca].clear(),G[Lca].insert(st[top]),G[st[top]].insert(Lca),st[top]=Lca;
				else
					G[Lca].insert(st[top]),G[st[top]].insert(Lca),top--;
			}
			G[h[i]].clear();st[++top]=h[i];
		}
	}
	for(int i=1;i<top;i++)
		G[st[i]].insert(st[i+1]),G[st[i+1]].insert(st[i]);
	top=0;
}
int main()
{
	scanf("%d%d%d",&n,&m,&q);
	for(int i=1;i<=m;i++)
		scanf("%d%d",&u,&v),put(u,v),put(v,u);
	for(int i=1;i<=n;i++)
		if(!dfn0[i])
			tarjan0(i,0);
	for(int i=1;i<=n;i++)
		if(!bi[i])
			sum++,sd(i);
	for(int i=1;i<=sum;i++)
		fa[i]=i;
	for(int i=1;i<=sum;i++)
		for(auto t:g[i])
			G0[i].insert(bi[t]),G0[bi[t]].insert(i),Union(i,bi[t]);
	for(int i=1;i<=sum;i++)
		if(!dep[i])
			dfs(i,0,1,i);
	for(int i=1;i<=q;i++)
	{
		scanf("%d%d",&N,&M);
		k=num=0;
		for(int j=1;j<=N;j++)
		{
			scanf("%d",&x[j]);
			x[j]=(x[j]+R)%n;
			if(x[j]==0)
				x[j]=n;
			h[++k]=bi[x[j]];
		}
		for(int j=1;j<=M;j++)
		{
			scanf("%d%d",&a[j],&b[j]);
			a[j]=(a[j]+R)%n,b[j]=(b[j]+R)%n;
			if(a[j]==0)
				a[j]=n;
			if(b[j]==0)
				b[j]=n;
			h[++k]=bi[a[j]],h[++k]=bi[b[j]];
		}
		sort(h+1,h+k+1);k=unique(h+1,h+k+1)-h-1;
		build();
		for(int j=1;j<=M;j++)
		{
			G[bi[a[j]]].insert(bi[b[j]]);
			G[bi[b[j]]].insert(bi[a[j]]);
		}
		for(int j=1;j<=k;j++)
			if(!dfn[h[j]])
				tarjan(h[j],0);
		getvis(bi[x[1]]);
		int ok=0;
		for(int j=1;j<=N;j++)
			if(!vis[bi[x[j]]])
				ok=1;
		if(ok==0)
			puts("YES"),R=(R+i)%n;
		else
			puts("NO");
	}

	return 0;
}
