namespace LCT
{
	int tg[M+5],tt[M+5],vl[M+5],nm[M+5],fl[M+5],pr[M+5],rv[M+5],sn[2][M+5];
	#define ls(x) sn[0][x]
	#define rs(x) sn[1][x]
	inline void Clr()
	{
		memset(tg,-1,sizeof(tg));
	}
	inline void Up(int x)
	{
		tt[x]=tt[ls(x)]+tt[rs(x)]+fl[x];
		vl[x]=vl[ls(x)]+vl[rs(x)]+fl[x]*nm[x];
	}
	inline void upd(int x,int v)
	{
		tg[x]=nm[x]=v;
		vl[x]=tt[x]*v;
	}
	inline void rev(int x)
	{
		rv[x]^=1;
		Swap(ls(x),rs(x));
	}
	inline void Dn(int x)
	{
		if(~tg[x])
		{
			upd(ls(x),tg[x]);
			upd(rs(x),tg[x]);
			tg[x]=-1;
		}
		if(rv[x])
		{
			rev(ls(x));
			rev(rs(x));
			rv[x]=0;
		}
	}
	inline int Sn(int x)
	{
		return rs(pr[x])==x;
	}
	inline int Ck(int x)
	{
		return ls(pr[x])==x||Sn(x);
	}
	inline void Rtt(int x)
	{
		int y=pr[x],z=pr[y],l=Sn(x),r=l^1,ch=Sn(y),p=sn[r][x];
		if(Ck(y))
		{
			sn[ch][z]=x;
		}
		sn[r][x]=y;
		sn[l][y]=p;
		if(p)
		{
			pr[p]=y;
		}
		pr[y]=x;
		pr[x]=z;
		Up(y);
		Up(x);
	}
	int top,stk[M+5];
	inline void Pre_Dn(int x)
	{
		for(;Ck(x);stk[++top]=x,x=pr[x]);
		for(stk[++top]=x;top;Dn(stk[top--]));
	}
	inline void Spy(int x)
	{
		int y;
		for(Pre_Dn(x);Ck(x);Rtt(x))
		{
			if(Ck(y=pr[x]))
			{
				Rtt(Sn(x)==Sn(y)?y:x);
			}
		}
	}
	inline void Acs(int x)
	{
		int y;
		for(y=0;x;Spy(x),rs(x)=y,Up(y=x),x=pr[x]);
	}
	inline void Mkr(int x)
	{
		Acs(x);
		Spy(x);
		rev(x);
	}
	inline void Spl(int x,int y)
	{
		Mkr(x);
		Acs(y);
		Spy(y);
	}
	inline int Fdr(int x)
	{
		Acs(x);
		Spy(x);
		for(;ls(x);x=ls(x));
		return x;
	}
	inline int Chk(int x,int y)
	{
		return Fdr(x)==Fdr(y);
	}
	inline void Lnk(int x,int y)
	{
		if(Chk(x,y))
		{
			return;
		}
		Mkr(x);
		pr[x]=y;
	}
	inline void Cut(int x,int y)
	{
		Spl(x,y);
		if(ls(y)==x)
		{
			ls(y)=0;
			pr[x]=0;
			Up(y);
		}
	}
	inline int Ask(int x,int y)
	{
		if(!Chk(x,y))
		{
			return -1;
		}
		Spl(x,y);
		return vl[y];
	}
	inline void Add(int x,int v,int t)
	{
		Mkr(x);
		fl[x]=v;
		nm[x]=t;
		Up(x);
	}
	inline void Cng(int x,int y,int v)
	{
		Spl(x,y);
		upd(y,v);
	}
}
struct Link
{
	int next,to;
}a[M+5];
struct Line
{
	int x,y,f;
}E[M+5];
int n,m,Q,tot,cnt,h[Mx+5],fa[Mx+5],pr[Mx+5],dp[Mx+5];
inline void ade(int x,int y)
{
	cnt++;
	a[cnt].next=h[x];
	a[cnt].to=y;
	h[x]=cnt;
}
inline void Ade(int x,int y)
{
	ade(x,y);
	ade(y,x);
}
inline int Gf(int x)
{
	return fa[x]==x?x:fa[x]=Gf(fa[x]);
}
inline void DFS(int x,int y,int z)
{
	int i,to;
	fa[x]=x;
	pr[x]=y;
	dp[x]=z;
	for(i=h[x];i;i=a[i].next)
	{
		if((to=a[i].to)^y)
		{
			DFS(to,x,z+1);
		}
	}
}
inline void Pts(int x)
{
	if(x)
	{
		writechar('Y','E');
		writechar('S',10);
