  for(int l=1;l<=n;l++){
    memset(bac,0,sizeof(bac));
    for(int r=l;r<=n;r++){
      int o=a[r];
      ++bac[o];
      if(bac[o]>bac[res]||(bac[o]==bac[res]&&o<res)) res=o;
      ans[res]++;
    }
  }
