#include<math.h>
#include<iostream>
#include<algorithm>
using namespace std;
int n,a[1005],vis[1005],cnt,ans;
void dfs(int s){
    vis[s]=1;
    int flag=0;
    for(int i=1;i<=n;i++){
        if(vis[i]==0){
            flag=1;
            vis[i]=1;
            int t=cnt;
            if(a[i]>a[s]&&s!=0)cnt++;
            dfs(i);
            cnt=t;
            vis[i]=0;
        }
    }
    if(flag==0){
        ans=max(ans,cnt);
    }
}
