using namespace std;

int main()
{
	int a,b,c,d,e,f;
	cin>>a>>b>>c>>d>>e>>f;
	if((a*c*e<b*d*f) || (!c&&d)|| (!a&&b&&d))
	{
		cout<<"Ron"<<endl;
		return 0;
	}
	else
	{
		cout<<"Hermione"<<endl;
		return 0;
	}
	return 0;
}
```

