        bis,bh1,yh1,bh2,yh2,bj:array[0..3010,0..3010] of longint;
procedure swap(var x,y:longint);//自定义交换函数
var
        t:longint;
begin
        t:=x;
        x:=y;
        y:=t;
end;
procedure link(x,y,z:longint);//链式前向星
begin
        inc(tot);
        ne[tot]:=he[x];
        he[x]:=tot;
        t[tot]:=y;
