using namespace std;
int main() {
	long long a, b, c;
	scanf("%lld%lld%lld", &a, &b, &c);
	if(b == a) {//第一种情况
		cout << "YES" << endl;
		return 0;
	}
	if(c==0) {//第二种情况
		cout << "NO" << endl;
		return 0;
	}
	if(a > b) {//第三种情况
		if(c > 0) {
			cout << "NO" << endl;
			return 0;
		}
		else {
			if((a-b) % abs(c) == 0) {
				cout << "YES" << endl;
				return 0;
			}	
			else {
				cout << "NO" << endl;
				return 0;
			}
		}
	}
	else {
		if(c < 0) {
			cout << "NO" << endl;
			return 0;
		}
		else {
			if((b-a) % c == 0) {
				cout << "YES" << endl;
				return 0;
			}
			else {
				cout << "NO" << endl;
				return 0;
			}
		}
	}
}
