	max_flow=val[tot];
	val[tot-1]=0,val[tot]=0;
	while(bfs(S_,T_,1))
		max_flow+=dinic(S_,0x3f3f3f3f,T_);
	cout<<max_flow*min(R,B)+(n-max_flow)*max(R,B)<<endl;
	char a=(R<B?'r':'b'),b=(a=='r'?'b':'r');
	for(int i=1;i<=n;i++) cout<<(val[nodes[i].pos]?a:b); 
}
