#include<iostream>
#include<cstdio>
#define in(x) scanf("%d",&x)
using namespace std;
int n,b,d;
int sum,ans;
int main(){
	in(n),in(b),in(d);//scanf读入
	for(int i=1;i<=n;i++){
		int a;
		in(a);
		if(a>b)continue;//如果超过b就跳过
		sum+=a;
		if(sum>d){//如果总量大于d就压缩，sum变为0
			ans++;sum=0;
		}
	}
	cout<<ans;
    return 0;
}
```

