int n,a,b,s,now,ans;
int main(){
    cin>>n>>a>>b;//读入 
    for(int i=1;i<=n;i++){
    	cin>>s;//读入当前产品重量 
    	if(s<=a) now+=s;//合格产品，总重量+=s 
		if(now>b) ans++,now=0;//需要特殊压缩 
	}
	cout<<ans;//输出特殊压缩次数 
    return 0;
}
