#include<bits/stdc++.h>
using namespace std;
int n,b,d,a[1000001],ans,s;
int main(){
    cin>>n>>b>>d;//n,b,d输入就不多讲了
    for(int i=1;i<=n;i++){
        cin>>a[i];
        if(a[i]>b){//判断是否超重，超重清零
            a[i]=0;
        }
    }
    for(int i=1;i<=n;i++){
        s+=a[i];//s累加
        if(s>d){
            s=0;//满箱清零
            ans++;//累加压缩次数
        }
    }
    cout<<ans;
    return 0;
}//结束程序
```

