bool isDigit(char c) 
{
	return c=='.' || c=='?' || c=='!';
}

int main()
{
	ios_base::sync_with_stdio(false);
	cin.tie(nullptr);

	int n; cin>>n;
	cin.get();
	string s; 
	getline(cin, s);
	//cout<<s;
	int cnt=0;
	vector<int> a;
	for(int i=0; i<s.size(); i++)
	{
		if(s[i]==' ')
		{
			if(i>0 && isDigit(s[i-1]))
				continue;
		}
		cnt++;
		if(isDigit(s[i]))
		{
			a.push_back(cnt);
			cnt=0;
		}
	}

	/*
	for(int i=0; i<a.size(); i++)
		cout<<a[i]<<" ";
	cout<<endl;
	*/

	for(int i=0; i<a.size(); i++)
		if(a[i]>n)
		{
			cout<<"Impossible"；
			return 0;
		}

	cnt=a[0];
	int ans=1;
	for(int i=1; i<a.size(); i++)
	{
		if(cnt+a[i]+1<=n)
			cnt+=a[i]+1;
		else
		{
			ans++;
