void pre(){
	for(rg int i=1;i<=n;i++) in[i]=out[i]=-1;
	for(rg int i=1;i<=n;i++) out[a[i]]=b[i],in[b[i]]=a[i];
	for(rg int i=1;i<=n;i++){
		if(out[i]!=-1 && in[i]==-1){
			rg int now=i;
			while(now && out[now]!=-1) now=out[now];
			if(!now) c10++;
		}
	}
	for(rg int i=1;i<=n;i++){
		if(in[i]!=-1 && out[i]==-1){
			rg int now=i;
			while(now && in[now]!=-1) now=in[now];
			if(!now) c01++;
		}
	}
	for(rg int i=1;i<=n;i++) if(a[i]==0 && b[i]==0) c00++;
	for(rg int i=1;i<=n;i++){
		if(in[i]==0){
			rg int now=i;
			while(now && out[now]!=-1) now=out[now];
			if(!now) c00++;
		}
	}
	for(rg int i=1;i<=n;i++){
		if(out[i]>0 && !vis[i]){
			rg int now=out[i];
			vis[now]=1;
