#include<algorithm>
#include<cstdio>
#include<cstdlib>
#include<cstring>
#include<cctype>
#include<cmath>
#include<ctime>
#include<iomanip>
#include<complex>
#include<climits>
#define re register
#define in inline
#define pi acos(-1.0)
#define inf 2147483640
using namespace std;
in int read()
{
    int sum=0,negative=1;
    char ch=getchar();
    while(ch<'0'||ch>'9')
    {
        if(ch=='-')negative=-1;
        ch=getchar();
    }
    while(ch>='0'&&ch<='9')
    {
        sum=sum*10+ch-'0';
        ch=getchar();
    }
    return sum*negative;
}
in void write(int X)
{
    if(X<0)putchar('-'),X=-X;
    if(X>=10)write(X/10);
    putchar(X%10+'0');
}
map<string,int>m;
char a[15],b[15];
int day[]={0,31,28,31,30,31,30,31,31,30,31,30,31};
void init(){//初始化
	m["monday"]=1;
	m["tuesday"]=2;
	m["wednesday"]=3;
	m["thursday"]=4;
	m["friday"]=5;
	m["saturday"]=6;
	m["sunday"]=7;
}
void scan(){//输入
	scanf("%s%s",a,b);
}
void judge(){//枚举
	int res=(m[b]-m[a]+7)%7;
	for(re int i=1;i<=12;i++){
		if(day[i]%7==res){
			puts("YES");
			return;
		}
	}
	puts("NO");
	return;
}
int main()
{
	ios::sync_with_stdio(false);
	init();
	scan();
	judge();
    return 0;
}
```

