#include<bits/stdc++.h>
using namespace std;
char c;
int ansA=0,ansD=0,n;
int main()
{
	cin>>n;
	cin.get();   //最好是加一个，不然可能会把换行当成字符。
	for(int i=1;i<=n;i++)
	{
		cin>>c;
		if(c=='A')ansA++;
		else ansD++;
	}
	if(ansA>ansD)puts("Anton");
	else if(ansA<ansD)puts("Danik");
	else puts("Friendship");    //判断并且输出。
	return 0;
}
```

