#include<cstdio>
using namespace std;
int main()
{
	int n,k;
	scanf("%d%d",&n,&k);//读入两个数
	k=240-k;//计算可供使用的时间
	int ans=0;//初始化
	for(int i=1;i<=n;i++)//穷尽所有的任务，从小到大贪心
	{
		if(5*i<=k)//如果还能做，那就做
		{
			k=k-5*i;//剩余的时间
			ans++; //已经做的任务个数+1
		}
	}
	printf("%d",ans);//输出
	return 0;
}
```
