int fa[M],dep[M],edge[M][3],went[M],mm[M];
int dd,n,u[3],s[3];

int tedge[M][3],tmm[M];
void askk(int x,int ff){
	if(went[x])return;
	went[x]=1;
	fa[x]=ff;
