signed main(void) { 
    n = read();
    for (int i = 1;i <= n;i++) x[i] = read(), t[i] = read();
    v = read();
    for (int i = 1;i <= n;i++) {
        if (v * t[i] + x[i] >= 0 && v * t[i] - x[i] >= 0) {
            a[++tot] = (node){v * t[i] + x[i], v * t[i] - x[i]};
        }
    }
    sort(a + 1, a + tot + 1);
    for (int i = 1;i <= tot;i++) {
        it = s.upper_bound(a[i].y);
        if (it != s.end()) s.erase(it);
        s.insert(a[i].y);
    }
    printf("%d ", s.size());
    s.clear();
    for (int i = 1;i <= n;i++) {
        a[i] = (node){v * t[i] + x[i], v * t[i] - x[i]};
    }
    sort(a + 1, a + n + 1);
    for (int i = 1;i <= n;i++) {
        it = s.upper_bound(a[i].y);
        if (it != s.end()) s.erase(it);
        s.insert(a[i].y);
    }
    printf("%d\n", s.size());
    return 0; 
}
```
